mapper.select:
SELECT  count(*)
FROM user
WHERE userId = #{userId}

SELECT u.userSeq, u.userId, u.userPwd, u.userName
FROM user u
WHERE userId = #{userId} AND userPwd = #{userPwd}

SELECT  u.userId, u.userPwd, u.userName
FROM user u
WHERE userId = #{userId}

mapper.update:
UPDATE user
SET
    mdfDt = sysdate()

    , userPwd = #{userPwd}


    , userName = #{userName}

WHERE userId = #{userId}
