mapper.select:
SELECT  count(*)
FROM usertable
WHERE userId = #{userId}

SELECT u.userSeq, u.userId, u.userPwd, u.userName
FROM usertable u
WHERE userId = #{userId} AND userPwd = #{userPwd}

mapper.update:
UPDATE usertable
SET
    MDF_DT = sysdate()

    , userPwd = #{userPwd}


    , userName = #{userName}

WHERE userId = #{userId}
